
import unittest
from valley import counting_valleys


class MayaCalculTest(unittest.TestCase):

    def test_case_1(self):
        trail = "UDDDUDUDUU"
        self.assertEqual(counting_valleys(trail, 1))

    def test_case_2(self):
        trail = "UDUUUDUDDD"
        self.assertEqual(counting_valleys(trail, 0))

    def test_case_3(self):
        trail = "UDDDUDUDUU"
        self.assertEqual(counting_valleys(trail, 1))

    def test_case_4(self):
        trail = "DDUDDUUDUU"
        self.assertEqual(counting_valleys(trail, 1))

    def test_case_5(self):
        trail = "DUDDDUUDUU"
        self.assertEqual(counting_valleys(trail, 2))

    def test_case_6(self):
        trail = 'DDUUDDDUDUUDUDDDUUDDUDDDUDDDUDUUDDUUDDDUDDDUDDDUUUDUDDDUDUDUDUUDDUDUDUDUDUUUUDDUDDUUDUUDUUDUUUUUUUUU'
        self.assertEqual(counting_valleys(trail, 2))

    def test_case_7(self):
        trail = 'UDUDDUUUDUDUDUUDUUDDDDDUDUDDDDUUDDUDDUUUUDUUDUDDDDUDUDUUUDDDUUUDUDDUUDDDUUDDUDDDUDUUDUUDUUDUDDDUUUUU'
        self.assertEqual(counting_valleys(trail, 6))

    def test_case_8(self):
        trail = 'DDUDUDDUDDUDDUUUUDUDDDUUDDUUDDDUUDDUUUUUUDUDDDDUDDUUDUUDUDUUUDUUUUUDDUDDDDUDDUDDDDUUUUDUUDUUDUUDUDDD'
        self.assertEqual(counting_valleys(trail, 5))

    def test_case_9(self):
        trail = ''
        self.assertEqual(counting_valleys(trail, 3))

    def test_case_10(self):
        trail = ''
        self.assertEqual(counting_valleys(trail, 10))
